import { useState } from 'react';
import { SortTypeModel } from '../@types/models';
import { setSortType } from '../redux/slices/sortSlice';

export const useSort = () => {

    const dispatch = useAppDispatch()

    const [sortState, setSortState] = useState([
        {
            value: true,
            label: 'Популярные',
            title: 'popular'
        },
        {
            value: false,
            label: 'Дешевле',
            title: 'cheaper'
        },
        {
            value: false,
            label: 'Дороже',
            title: 'expensive'
        },
        {
            value: false,
            label: 'По алфавиту',
            title: 'alphabetically'
        },
    ]);

    const onClickSortItem = (obj: SortTypeModel) => {
        setSortType
    }

    const getSortParam = () => {
        return sortState.find((obj) => obj.value)!.title
    }

    return { sortState, onClickSortItem, getSortParam }
}
