import { stroykaApi } from '../stroyka.api';
import { ProductModel } from '../../@types/models';

const injectedBrands = stroykaApi.injectEndpoints({
    endpoints: (builder) => ({
        getBrands: builder.query<any, string>({
            query: (type) => ({
                url: `brands`,
                params: {
                    type: type,
                }
            }),
        }),

    }),
    overrideExisting: false,
})

export const {
    useGetBrandsQuery
} = injectedBrands;
