import { useIsAuthQuery } from "../redux/injected/injectedAuth"

export const useIsAuth = () => {
    const { data, isSuccess } = useIsAuthQuery()


    useEffect(() => {
        if (data.message === "ok") {
            return true
        } else {
            return false
        }
    }, isSuccess)

}
