import { createApi, fetchBaseQuery, retry } from '@reduxjs/toolkit/query/react';

const baseQuery = fetchBaseQuery({
    baseUrl: '/',
    prepareHeaders: (headers, { getState }) => {
      const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoiNjNlYTc0YzgzOGYzOGM4MjQwZGJiZDdjIiwiZXhwaXJlc19hdCI6MTY3NjQ2OTQ0Ni4zMTcyNjV9.k-D7_hQ4aPy_uggKxaZdW92FKPddQv9V0Nus3hLmL6Y"
      if (token) {
        headers.set('authorization', `Bearer ${token}`)
      }
  
      return headers
    },
  })

const url = `http://127.0.0.1/api/`;

export const stroykaApi = createApi({
    reducerPath: 'stroykaApi',
    tagTypes: ['Cart', 'Orders', 'Category', 'Discount', 'PopularProducts', 'Product', 'Reviews', 'Searched', 'SimilarProducts'],
    baseQuery: retry(fetchBaseQuery({ baseUrl: url }), { maxRetries: 1 }),
    refetchOnFocus: true,
    endpoints: () => ({}),
})

export const { } = stroykaApi;
