import { useState } from 'react';
import { SortTypeModel } from '../@types/models';
import { setSortType } from '../redux/slices/sortSlice';
import { useAppDispatch } from '../redux/store';

export const useSort = () => {

    const dispatch = useAppDispatch();

    const isOpenLocateModal = useSelector((state: RootState) => state.popupSlice.isOpenChengeLocate);

    const [sortState, setSortState] = useState([
        {
            value: true,
            label: 'Популярные',
            title: 'popular'
        },
        {
            value: false,
            label: 'Дешевле',
            title: 'cheaper'
        },
        {
            value: false,
            label: 'Дороже',
            title: 'expensive'
        },
        {
            value: false,
            label: 'По алфавиту',
            title: 'alphabetically'
        },
    ]);

    const onClickSortItem = (obj: SortTypeModel) => {
        dispatch(setSortType(obj))
    }

    const getSortParam = () => {
        return sortState.find((obj) => obj.value)!.title
    }

    return { c, onClickSortItem, getSortParam }
}
