import { FC, useState, useEffect } from 'react';
import { useSort } from '../../hooks/useSort';
import MarkIcon from '../../Icons/MarkIcon';
import clsx from 'clsx';
import style from './Sort.module.scss';
import { SortTypeModel } from '../../@types/models';

const Sort: FC = () => {

    const { sortState, onClickSortItem } = useSort();
    const [selected, setSelected] = useState<boolean[]>([]);
    const [dropdown, setDropdown] = useState(false);

    useEffect(() => {
        // const selectedArr = sortState.map((obj) => obj.value)
        // setSelected(selectedArr)
        // setDropdown(false)
        console.log(sortState)
    }, [sortState])

    const toggleDropdown = () => {
        setDropdown(!dropdown)
    }

    const test: SortTypeModel[] = [
        {
            label: 'Популярные',
            title: 'popular'
        },
        {
            label: 'Дешевле',
            title: 'cheaper'
        },
        {
            label: 'Дороже',
            title: 'expensive'
        },
        {
            label: 'По алфавиту',
            title: 'alphabetically'
        }
    ]

    return (
        <div className={style['select']}>
            <button
                className={clsx(style['select-btn'], {
                    [style['select-btn--active']]: dropdown
                })}
                onClick={toggleDropdown}
            >{sortState.label} <MarkIcon /></button>
            <ul className={clsx(style['sort'], {
                [style['sort--active']]: dropdown
            })}>
                <li className={style['sort__item']}>
                    <button className={clsx(style['sort__btn'], {
                        [style['sort__btn--active']]: sortState.label === test[0].label
                    })}
                        onClick={() => onClickSortItem(test[0])}>Популярные</button>
                </li>
                <li className={style['sort__item']}>
                    <button className={clsx(style['sort__btn'], {
                        [style['sort__btn--active']]: sortState.label === test[1].label
                    })}
                        onClick={() => onClickSortItem(test[1])}>Дешевле</button>
                </li>
                <li className={style['sort__item']}>
                    <button className={clsx(style['sort__btn'], {
                        [style['sort__btn--active']]: sortState.label === test[0].label
                    })}
                        onClick={() => onClickSortItem(test[2])}>Дороже</button>
                </li>
                <li className={style['sort__item']}>

                    <button className={clsx(style['sort__btn'], {
                        [style['sort__btn--active']]: sortState.label === test[3].label
                    })}
                        onClick={() => onClickSortItem(test[3])}>По алфавиту</button>
                </li>
            </ul>
        </div>
    );
}

export default Sort;
