import { stroykaApi } from '../stroyka.api';
import { CartProductModel } from '../../@types/models';

const injectedCart = stroykaApi.injectEndpoints({
    endpoints: (builder) => ({
        getCart: builder.query<CartProductModel[], any | undefined>({
            query: () => ({
                url: 'cart',
                prepareHeaders: (headers: any) => {
                        token.set("auth-token", document.cookie.split('').splice(6, document.cookie.split('').length).join(''));
                    return token;
                },
            }),
            providesTags: (result) =>
                result
                    ? [...result.map(({ id }) => ({ type: 'Cart' as const, id })), 'Cart']
                    : ['Cart'],
        }),
        addCartItem: builder.mutation({
            query: ({ id, ...data }) => ({
                url: `cart`,
                method: 'POST',
                body: data,
            }),
            invalidatesTags: ['Cart'],
        }),
        changeCartItem: builder.mutation({
            query: ({ id, ...data }) => ({
                url: `cart/${id}`,
                method: 'PUT',
                body: data,
            }),
            invalidatesTags: ['Cart'],
        }),
        deleteCartItem: builder.mutation({
            query: (id) => ({
                url: `cart/${id}`,
                method: 'DELETE',
            }),
            invalidatesTags: ['Cart'],
        }),
    }),
    overrideExisting: false,
})

export const {
    useAddCartItemMutation,
    useGetCartQuery,
    useLazyGetCartQuery,
    useChangeCartItemMutation,
    useDeleteCartItemMutation
} = injectedCart;
