import { stroykaApi } from '../stroyka.api';
import { FooterModel } from '../../@types/models';


const injectedFooter = stroykaApi.injectEndpoints({
    endpoints: (builder) => ({
        getFooterItems: builder.query<FooterModel[], void>({
            query: () => ({
                url: 'footer/',
            }),
        }),
    }),
    overrideExisting: false,
})

export const { useGetFooterItemsQuery } = injectedFooter;
