    const [isCorrectToken, setIsCorrectToken] = useState<boolean | null>(null);


        if (isSuccess) {
            setIsCorrectToken(data.message === "ok")
        } else if (isError) {
            setIsCorrectToken(false)
        }



    return isCorrectToken
}
