import { FC } from 'react';
import { useScrollToTop } from '../../hooks/useScrollToTop';
import { useForm } from 'react-hook-form';
import style from './Registration.module.scss';

type Inputs = {
    name: string,
    surname: string;
    tel: number;
    email: string;
    birthDay: string;
    password: string;
    cpassword: string;
};

const Registration: FC = () => {

    useScrollToTop();

    const {
        register,
        handleSubmit,
        reset,
        getValues,
        formState: {
            errors
        }
    } = useForm<Inputs>({ mode: 'onChange' });

    const onSubmit = (data: Inputs) => {
        console.log(data)
        reset()
    }

    return (
        <section className={style['registration']}>
            <div className={style['registration-wrapper']}>
                <h1 className={style['registration__title']}>Регистрация</h1>
                <div className={style['registration-content']}>
                    <h2 className={style['registration-content__title']}>Личная информация</h2>
                    
                </div>
            </div>
        </section>
    );
}

export default Registration;
