import { useEffect, useState } from "react"
import { useIsAuthQuery } from "../redux/injected/injectedAuth"

export const useIsAuth = () => {

    const { data, isSuccess } = useIsAuthQuery()
    const [isCorrectToken, setIsCorrectToken] = useState(nu);

    useEffect(() => {
        if (isSuccess) {
            if (data.message === "ok") {
                setIsCorrectToken( true)
            } else {
                setIsCorrectToken( false)
            }
        }
    }, [isSuccess])

}
